mapper.insert:
INSERT INTO user
(
  user_id
, user_password
, user_name
, user_nick
, user_email
, user_type
)
VALUES
(
  #{userId}
, #{userPassword}
, #{userName}
, #{userNick}
, #{userEmail}
, #{userType}
)

mapper.update:
UPDATE user
   SET user_nick = #{userNick}
     , user_email = #{userEmail}
     , user_name = #{userName}

     , user_password = #{userPassword}

 WHERE user_id = #{userId}
